import json
import base64
import boto3

dynamodb = boto3.resource('dynamodb')
table = dynamodb.Table('KinesisDataTable')

def lambda_handler(event, context):
    for record in event['Records']:
        try:
            payload = base64.b64decode(record['kinesis']['data']).decode('utf-8')
            data = json.loads(payload)
            table.put_item(Item={ 'id': str(data['id']), 'message': data['message'], 'timestamp': str(data['timestamp'])})
            print(f"Data inserted into DynamoDB: {data}")
        except Exception as e:
            print(f"Error processing record: {str(e)}")
    return {'statusCode': 200, 'body': 'Success'}

import json
import boto3

ses = boto3.client('ses')

def lambda_handler(event, context):
    for record in event['Records']:
        event_name = record['eventName']
        old_image = record['dynamodb'].get('OldImage', {})
        new_image = record['dynamodb'].get('NewImage', {})
        email_subject = f"DynamoDB {event_name} Event Detected"
        email_body = f"<html><head></head><body><h2>DynamoDB {event_name} Event</h2><p>A {event_name} event occurred in your DynamoDB table.</p>"
        if new_image:
            email_body += "<h3>New Data:</h3><ul>"
            for key, value in new_image.items():
                email_body += f"<li><strong>{key}:</strong> {value['S']}</li>"
            email_body += "</ul>"
        if old_image:
            email_body += "<h3>Old Data:</h3><ul>"
            for key, value in old_image.items():
                email_body += f"<li><strong>{key}:</strong> {value['S']}</li>"
            email_body += "</ul>"
        email_body += "</body></html>"
        try:
            ses.send_email(Source='shuolin96@gmail.com', Destination={'ToAddresses': ['hu.shuo@northeastern.edu']}, Message={'Subject': {'Data': email_subject}, 'Body': {'Html': {'Data': email_body}, 'Text': {'Data': f'DynamoDB {event_name} event occurred.'}}})
        except Exception as e:
            print(f"Error sending email: {str(e)}")
    return {'statusCode': 200, 'body': 'Email sent successfully.'}
